    }

    return count
}

fun main() {
    println(findMaxConsecutiveOnes(intArrayOf(1, 1, 0, 1, 1, 1)))
//    println(findMaxConsecutiveOnes(intArrayOf(1,0,1,1,0,1)))
    println(findMaxConsecutiveOnes(intArray))
    //1,1,0,1,1,1
}